CREATE TABLE Customers (\n    CustomerID int,\n    CustomerName varchar(255),\n    ContactName varchar(255),\n    Address varchar(255),\n    City varchar(255),\n    PostalCode varchar(255),\n    Country varchar(255),\n    PRIMARY KEY (CustomerID)\n);\n\nCREATE TABLE Orders (\n    OrderID int,\n    CustomerID int,\n    CustomerID int, # Same name as foreign key\n    EmployeeID int,\n    OrderDate datetime,\n    ShipperID int,\n    PRIMARY KEY (OrderID),\n    FOREIGN KEY (CustomerID) REFERENCES Customers(CustomerID)\n);\n\nCREATE TABLE Shippers (\n    ShipperID int,\n    ShipperName varchar(255),\n    Phone varchar(255),\n    PRIMARY KEY (ShipperID)\n);