SELECT \n  page.page_namespace, \n  pagelinks.pl_namespace, \n  COUNT(*) \nFROM \n  page, \n  pagelinks\nWHERE \n  (page.page_namespace <= 3 OR page.page_namespace = 12 OR page.page_namespace = 13)\n  AND \n  (pagelinks.pl_namespace <= 3 OR pagelinks.pl_namespace = 12 OR pagelinks.pl_namespace = 13)\n  AND \n  page.page_is_redirect = 0\n  AND \n  pagelinks.pl_from = page.page_id\nGROUP BY \n  ROW(page.page_namespace, pagelinks.pl_namespace)\nORDER BY \n  page.page_namespace, \n  pagelinks.pl_namespace ASC;