-- Create a new column for the ID\nALTER TABLE your_table ADD id NUMBER;\n\n-- Create a sequence for the auto-incrementing ID\nCREATE SEQUENCE your_table_id_seq START WITH 1 INCREMENT BY 1;\n\n-- Update existing rows with unique IDs\nDECLARE\n  v_id NUMBER;\nBEGIN\n  FOR cur_rec IN (SELECT ROWID FROM your_table) LOOP\n    v_id := your_table_id_seq.NEXTVAL;\n    UPDATE your_table SET id = v_id WHERE ROWID = cur_rec.ROWID;\n  END LOOP;\nEND;\n\n-- Create a trigger to auto-increment the ID for new rows\nCREATE TRIGGER your_table_id_trg\nBEFORE INSERT ON your_table\nFOR EACH ROW\nBEGIN\n  SELECT your_table_id_seq.NEXTVAL INTO :NEW.id FROM DUAL;\nEND;