CREATE TABLE Hierarchy_Nodes (\n  Node_ID INT PRIMARY KEY,\n  Node_Type VARCHAR(50)  -- e.g. 'Team', 'Person'\n);\n\nCREATE TABLE Hierarchy_Relationships (\n  Relationship_ID INT PRIMARY KEY,\n  Parent_Node_ID INT,\n  Child_Node_ID INT,\n  Effective_Date DATE,\n  End_Date DATE,\n  FOREIGN KEY (Parent_Node_ID) REFERENCES Hierarchy_Nodes(Node_ID),\n  FOREIGN KEY (Child_Node_ID) REFERENCES Hierarchy_Nodes(Node_ID)\n);