CREATE TABLE dvds (\n    title tsvector,\n    actors tsvector,\n    description tsvector\n);\n\nCREATE INDEX idx_dvds_title ON dvds USING GIN (title);\nCREATE INDEX idx_dvds_actors ON dvds USING GIN (actors);\nCREATE INDEX idx_dvds_description ON dvds USING GIN (description);\n\nSELECT * FROM dvds \nWHERE to_tsvector('Keanu Reeves') @@ title \n   OR to_tsvector('Keanu Reeves') @@ actors \n   OR to_tsvector('Keanu Reeves') @@ description;