SELECT *\nFROM Company\nWHERE (CompanyName, CompanyCity, CompanyState) IN (\n  SELECT CompanyName, CompanyCity, CompanyState\n  FROM Company\n  GROUP BY CompanyName, CompanyCity, CompanyState\n  HAVING COUNT(*) > 1\n)\nOR (CompanyAddress, CompanyCity, CompanyState) IN (\n  SELECT CompanyAddress, CompanyCity, CompanyState\n  FROM Company\n  GROUP BY CompanyAddress, CompanyCity, CompanyState\n  HAVING COUNT(*) > 1\n)