DECLARE @dbList TABLE (dbname sysname);\nINSERT INTO @dbList (dbname) VALUES \n    ('TF7-User-Demo-ScheduledRestore-03'),\n    ('TF7-TestDatabase-ScheduledRestore');\n\nDECLARE @sql NVARCHAR(MAX);\n\nDECLARE cur CURSOR FOR SELECT dbname FROM @dbList;\nOPEN cur;\n\nFETCH NEXT FROM cur INTO @dbname;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @sql = N'SELECT ''' + @dbname + ''' AS DBCatalog, COUNT(*) AS CNT \n                 FROM [' + @dbname + '].[dbo].[LinkedDocumentFolderTable] \n                 WHERE IsUnmanagedFolder = 1';\n\n    EXEC sp_executesql @sql;\n\n    FETCH NEXT FROM cur INTO @dbname;\nEND\n\nCLOSE cur;\nDEALLOCATE cur;