BEGIN\n  DBMS_RESOURCE_MANAGER.CREATE_PLAN(\n    plan => 'LIMIT_EXEC_TIME',\n    comment => 'Limit execution time to 60 seconds');\n\n  DBMS_RESOURCE_MANAGER.CREATE_RESOURCE(\n    resource => 'EXEC_TIME',\n    plan => 'LIMIT_EXEC_TIME',\n    allocation => 1,\n    limit => 60);\n\n  DBMS_RESOURCE_MANAGER.CREATE_RULE(\n    rule => 'KILL_LONG_QUERIES',\n    rule_type => DBMS_RESOURCE_MANAGER.SESSION_LONGOPS,\n    condition => 'elapsed_time > 60',\n    action => 'KILL SESSION');\nEND;\n/