SELECT \n  CASE \n    WHEN EXISTS (\n      SELECT 1 \n      FROM your_table \n      WHERE \n        (new_range_start, new_range_end) BETWEEN (range_start, range_end) \n        OR (range_start, range_end) BETWEEN (new_range_start, new_range_end)\n        OR (new_range_start <= range_end AND new_range_end >= range_start)\n    ) THEN 'IN LIST'\n    ELSE 'NOT IN LIST'\n  END AS result