WITH TextSearchResults AS (\n  SELECT t.*, ftt.Rank\n  FROM YourTable t\n  INNER JOIN FREETEXTTABLE(YourTable, *, @searchString) ftt\n  ON t.PK = ftt.[KEY]\n)\nSELECT *\nFROM (\n  SELECT PK, columns...\n  FROM TextSearchResults\n  UNION ALL\n  SELECT PK, columns...\n  FROM YourTable\n  WHERE PK = TRY_CAST(@searchString AS INT)\n) AS CombinedResults\nORDER BY CASE\n  WHEN PK = TRY_CAST(@searchString AS INT) THEN 0\n  ELSE Rank\nEND;