-- Add a new column new_ID and update it with values from ID column + 1\nALTER TABLE your_table ADD new_ID INT;\nUPDATE your_table SET new_ID = ID + 1;\n\n-- Drop the ID column\nALTER TABLE your_table DROP COLUMN ID;\n\n-- Rename new_ID to ID\nEXEC sp_rename 'your_table.new_ID', 'ID', 'COLUMN';\n\n-- Set Identity on the new column 'ID'\nALTER TABLE your_table ALTER COLUMN ID INT IDENTITY(1,1);