-- Change the data type of the primary key column to uniqueidentifier\nALTER TABLE [YourTableName]\nALTER COLUMN [YourColumnName] uniqueidentifier NOT NULL;\n\n-- Drop the existing primary key constraint\nALTER TABLE [YourTableName]\nDROP CONSTRAINT [YourPrimaryKeyConstraintName];\n\n-- Create a new primary key constraint with the updated data type\nALTER TABLE [YourTableName]\nADD CONSTRAINT [YourPrimaryKeyConstraintName]\nPRIMARY KEY CLUSTERED ([YourColumnName] ASC);\n\n-- Update foreign key references\nDECLARE @sql nvarchar(max) = '';\n\nSELECT @sql += 'ALTER TABLE [' + OBJECT_NAME(fk.parent_object_id) + ']\nDROP CONSTRAINT [' + fk.name + '];\n\nALTER TABLE [' + OBJECT_NAME(fk.parent_object_id) + ']\nADD CONSTRAINT [' + fk.name + ']\nFOREIGN KEY ([' + col.name + '])\nREFERENCES [' + OBJECT_NAME(fk.referenced_object_id) + ']([' + refcol.name + ']);'\nFROM sys.foreign_keys fk\nINNER JOIN sys.foreign_key_columns fkc ON fk.object_id = fkc.constraint_object_id\nINNER JOIN sys.columns col ON fkc.parent_object_id = col.object_id AND fkc.parent_column_id = col.column_id\nINNER JOIN sys.columns refcol ON fkc.referenced_object_id = refcol.object_id AND fkc.referenced_column_id = refcol.column_id\nWHERE fk.referenced_object_id = OBJECT_ID('YourTableName');\n\nEXEC sp_executesql @sql;