CREATE TABLE #Dates (\n    DateString nvarchar(10),\n    DateDate datetime\n);\n\nINSERT INTO #Dates (DateString, DateDate)\nVALUES ('12/25/2007', '2007-12-25 00:00:00');\n\nCREATE INDEX idx_DateString ON #Dates (DateString);\nCREATE INDEX idx_DateDate ON #Dates (DateDate);\n\n-- Query 1: Using DateString\nSELECT * FROM #Dates\nWHERE DateString = '12/25/2007';\n\n-- Query 2: Using DateDate\nSELECT * FROM #Dates\nWHERE DateDate = '2007-12-25 00:00:00';\n\nDROP TABLE #Dates;