WITH RankedBills AS (\n  SELECT \n    BillId,\n    Estimated,\n    InvoiceDate,\n    ROW_NUMBER() OVER (ORDER BY InvoiceDate DESC) AS RowNumber\n  FROM \n    Bills\n  WHERE \n    Estimated = 1\n)\nSELECT \n  b1.BillId,\n  b1.Estimated,\n  b1.InvoiceDate,\n  b2.BillId,\n  b2.Estimated,\n  b2.InvoiceDate\nFROM \n  RankedBills b1\n  JOIN RankedBills b2 ON b1.RowNumber = b2.RowNumber - 1;