SELECT \n  (SELECT PatientVisitID FROM tblSignature WHERE PatientID = 12345 ORDER BY TreatAuthDate DESC LIMIT 1) AS TreatAuthPatientVisitID,\n  (SELECT PatientVisitID FROM tblSignature WHERE PatientID = 12345 ORDER BY HIPAADate DESC LIMIT 1) AS HIPAAPatientVisitID,\n  (SELECT PatientVisitID FROM tblSignature WHERE PatientID = 12345 ORDER BY DrugTestDate DESC LIMIT 1) AS DrugTestPatientVisitID