CREATE TABLE Time_Dimension (\n    Time_Key INT,\n    Hour INT,\n    -- other columns...\n);\n\nCREATE TABLE Business_Hours (\n    Day_Of_Week INT,\n    Start_Hour INT,\n    End_Hour INT\n);\n\n-- Sample data for Business_Hours table\nINSERT INTO Business_Hours (Day_Of_Week, Start_Hour, End_Hour)\nVALUES\n    (1, 8, 17),  -- Monday, 8am-5pm\n    (2, 8, 17),  -- Tuesday, 8am-5pm\n    -- ... other days of the week ...\n\n-- Create a view or calculated measure for business hours\nCREATE VIEW Business_Hour_Flag AS\nSELECT \n    T.Time_Key,\n    CASE \n        WHEN B.Day_Of_Week = DATEPART(dw, T.Time_Key) \n             AND T.Hour BETWEEN B.Start_Hour AND B.End_Hour \n        THEN 1 \n        ELSE 0 \n    END AS Is_Business_Hour\nFROM Time_Dimension T\nLEFT JOIN Business_Hours B ON T.Time_Key = B.Day_Of_Week;