-- Create a new backup job\nUSE msdb\nGO\nEXEC sp_add_job @job_name = 'Backup_Database'\n\n-- Add a step to the job to backup the database\nEXEC sp_add_jobstep @job_id = @job_id, \n                    @step_name = 'Backup_Database', \n                    @subsystem = 'TSQL', \n                    @command = 'BACKUP DATABASE [YourDatabaseName] TO DISK = ''C:\Backup\YourDatabaseName.bak'''\n\n-- Schedule the job to run daily at 2am\nEXEC sp_add_jobschedule @job_id = @job_id, \n                        @name = 'Daily_Backup', \n                        @freq_type = 4,  -- daily\n                        @freq_interval = 1, \n                        @freq_recurrence_factor = 1, \n                        @active_start_time = 20000  -- 2am