SET @sql = NULL;\n\nSELECT \n  GROUP_CONCAT(\n    CONCAT('tabx.', column_name) \n  ) INTO @sql \nFROM \n  information_schema.columns \nWHERE \n  table_name = 'tabx';\n\nSET @sql = CONCAT(@sql, ', ');\n\nSELECT \n  GROUP_CONCAT(\n    CONCAT('taby.', column_name) \n  ) INTO @sql \nFROM \n  information_schema.columns \nWHERE \n  table_name = 'taby';\n\nSET @sql = CONCAT('SELECT ', @sql, ' FROM tabx, taby WHERE ...');\n\nPREPARE stmt FROM @sql;\nEXECUTE stmt;