-- Create a test package\nCREATE OR REPLACE PACKAGE test_package AS\n  PROCEDURE run_tests;\nEND test_package;\n\n-- Create a test package body\nCREATE OR REPLACE PACKAGE BODY test_package AS\n  PROCEDURE run_tests IS\n  BEGIN\n    ut.assert_true('Test condition', 1 = 1);\n  END run_tests;\nEND test_package;\n\n-- Run the test\nBEGIN\n  test_package.run_tests;\n  ut.result();\nEND;