DECLARE @N int = 6\nDECLARE @RowID int = 14\n\nSELECT Value\nFROM (\n    SELECT *\n    FROM MyTable\n    WHERE RowID = @RowID\n) AS SourceTable\nUNPIVOT (\n    Value\n    FOR ColumnName IN ([Column1], [Column2], ..., [ColumnN]) -- specify all column names\n) AS UnpivotTable\nWHERE ColumnName = (\n    SELECT COLUMN_NAME\n    FROM INFORMATION_SCHEMA.COLUMNS\n    WHERE TABLE_NAME = 'MyTable' AND ORDINAL_POSITION = @N\n)