CREATE FUNCTION dbo.BinaryStringToVarbinary (@binaryString VARCHAR(MAX))\nRETURNS VARBINARY(MAX)\nAS\nBEGIN\n    DECLARE @varbinary VARBINARY(MAX);\n    SET @varbinary = 0x;\n    DECLARE @i INT = 0;\n    WHILE @i < LEN(@binaryString)\n    BEGIN\n        SET @varbinary = @varbinary + CONVERT(VARBINARY(1), CONVERT(INT, SUBSTRING(@binaryString, @i, 8), 2));\n        SET @i = @i + 8;\n    END\n    RETURN @varbinary;\nEND\nGO\n\nCREATE FUNCTION dbo.BinaryStringToHex (@binaryString VARCHAR(MAX))\nRETURNS VARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @varbinary VARBINARY(MAX);\n    SET @varbinary = dbo.BinaryStringToVarbinary (@binaryString);\n    RETURN master.dbo.fn_varbintohexstr (@varbinary);\nEND\nGO\n\nSELECT dbo.BinaryStringToHex ('0000100101000101011101011110');