SELECT p.*\nFROM Projects p\nLEFT JOIN Roofing r ON p.projectId = r.projectId AND r.status <> 'completed'\nLEFT JOIN Siding s ON p.projectId = s.projectId AND s.status <> 'completed'\nLEFT JOIN Gutters g ON p.projectId = g.projectId AND g.status <> 'completed'\nLEFT JOIN Misc m ON p.projectId = m.projectId AND m.status <> 'completed'\nWHERE r.projectId IS NULL AND s.projectId IS NULL AND g.projectId IS NULL AND m.projectId IS NULL;