DELIMITER //\nCREATE PROCEDURE clone_record(IN table_name VARCHAR(255), IN id INT)\nBEGIN\n  DECLARE column_list TEXT;\n  SELECT GROUP_CONCAT(COLUMN_NAME) INTO column_list\n  FROM INFORMATION_SCHEMA.COLUMNS\n  WHERE TABLE_NAME = table_name AND TABLE_SCHEMA = DATABASE();\n\n  SET @sql = CONCAT('INSERT INTO ', table_name, ' (', column_list, ') SELECT ', column_list, ' FROM ', table_name, ' WHERE id = ', id);\n  PREPARE stmt FROM @sql;\n  EXECUTE stmt;\n  DEALLOCATE PREPARE stmt;\nEND//\nDELIMITER ;