DECLARE @datePart VARCHAR(2)\nDECLARE @dateParameter INT\n\nSELECT @datePart = 'dd'\nSELECT @dateParameter = 28\n\nSELECT\n    *\nFROM\n    MyTable\nWHERE\n    CASE @datePart\n        WHEN 'dd' THEN DATEDIFF(dd, MyTable.MyDate, GETDATE())\n        WHEN 'ww' THEN DATEDIFF(ww, MyTable.MyDate, GETDATE())\n        WHEN 'mm' THEN DATEDIFF(mm, MyTable.MyDate, GETDATE())\n        WHEN 'qq' THEN DATEDIFF(qq, MyTable.MyDate, GETDATE())\n        WHEN 'yy' THEN DATEDIFF(yy, MyTable.MyDate, GETDATE())\n        ELSE NULL\n    END < @dateParameter