CREATE SERVER AUDIT [MyAudit]\nTO FILE = 'C:\AuditLogs\MyAudit.log'\nWITH (FORMAT = XML, ENCRYPTION = ON);\n\nCREATE SERVER AUDIT SPECIFICATION [MyAuditSpec]\nFOR SERVER AUDIT [MyAudit]\nADD (APPLICATION_LOGIN),\nADD (FAILED_LOGIN),\nADD (SUCCESSFUL_LOGIN);\n\nALTER SERVER AUDIT [MyAudit] ENABLE;\nALTER SERVER AUDIT SPECIFICATION [MyAuditSpec] ENABLE;