DECLARE @search_string NVARCHAR(50) = 'your_misspelled_string';\nDECLARE @sql NVARCHAR(MAX) = '';\n\nSELECT @sql += 'SELECT ''' + TABLE_NAME + '.' + COLUMN_NAME + ''' AS column_name, * \n               FROM ' + TABLE_NAME + ' \n               WHERE ' + COLUMN_NAME + ' LIKE ''' + @search_string + '%'';' + CHAR(13)\nFROM INFORMATION_SCHEMA.COLUMNS\nWHERE DATA_TYPE IN ('varchar', 'nvarchar', 'char', 'nchar', 'text', 'ntext');\n\nEXEC sp_executesql @sql;