CREATE TABLE Dim_Case (\n  CaseID INT PRIMARY KEY,\n  CaseType VARCHAR(50),\n  CaseStatus VARCHAR(50),\n  DateOpened DATE\n);\n\nCREATE TABLE Dim_EventType (\n  EventTypeID INT PRIMARY KEY,\n  EventTypeName VARCHAR(50)\n);\n\nCREATE TABLE Dim_Date (\n  DateKey INT PRIMARY KEY,\n  Date DATE,\n  Hour INT,\n  DayOfWeek INT\n);\n\nCREATE TABLE Dim_Department (\n  DepartmentID INT PRIMARY KEY,\n  DepartmentName VARCHAR(50)\n);\n\nCREATE TABLE Fact_CaseEvents (\n  CaseID INT,\n  EventTypeID INT,\n  DateKey INT,\n  DepartmentID INT,\n  EventDateTime DATETIME,\n  FOREIGN KEY (CaseID) REFERENCES Dim_Case(CaseID),\n  FOREIGN KEY (EventTypeID) REFERENCES Dim_EventType(EventTypeID),\n  FOREIGN KEY (DateKey) REFERENCES Dim_Date(DateKey),\n  FOREIGN KEY (DepartmentID) REFERENCES Dim_Department(DepartmentID)\n);\n\nCREATE TABLE Fact_CaseProcessing (\n  CaseID INT,\n  DateKey INT,\n  DepartmentID INT,\n  ProcessingTime INT,\n  FOREIGN KEY (CaseID) REFERENCES Dim_Case(CaseID),\n  FOREIGN KEY (DateKey) REFERENCES Dim_Date(DateKey),\n  FOREIGN KEY (DepartmentID) REFERENCES Dim_Department(DepartmentID)\n);