SelectCommand="SELECT usrs.UserID, usrs.UserName, usrs.FirstName, usrs.LastName, jndt.PropertyValue AS JoinDate, rgn.PropertyValue AS Region, cty.PropertyValue AS City, spnsrnm.PropertyValue AS SponsorName \nFROM            dbo.Users AS usrs \nINNER JOIN      dbo.UserProfile AS jndt ON jndt.PropertyDefinitionID = 412 AND usrs.UserID = jndt.UserID \nINNER JOIN      dbo.UserProfile AS cty ON cty.PropertyDefinitionID = 27 AND usrs.UserID = cty.UserID \nINNER JOIN      dbo.UserProfile AS rgn ON rgn.PropertyDefinitionID = 28 AND usrs.UserID = rgn.UserID \nINNER JOIN      dbo.UserProfile AS spnsrnm ON spnsrnm.PropertyDefinitionID = 421 AND usrs.UserID = spnsrnm.UserID \nWHERE CONVERT(varchar(50), usrs.UserID) LIKE '%{0}%' \nOR usrs.UserName LIKE '%{0}%' \nOR usrs.FirstName LIKE '%{0}%' \nOR usrs.LastName LIKE '%{0}%' \nOR jndt.PropertyValue LIKE '%{0}%' \nOR rgn.PropertyValue LIKE '%{0}%' \nOR cty.PropertyValue LIKE '%{0}%' \nOR spnsrnm.PropertyValue LIKE '%{0}%'"