-- Create a partition function\nCREATE PARTITION FUNCTION pf_userid (BIGINT) \nAS RANGE RIGHT FOR VALUES \n    (1000, 2000, 3000, 4000, 5000, 6000, 7000, 8000, 9000, 10000);\n\n-- Create a partition scheme\nCREATE PARTITION SCHEME ps_userid \nAS PARTITION pf_userid \nALL TO ('PRIMARY');\n\n-- Apply the partition scheme to your table\nALTER TABLE your_table \nADD CONSTRAINT pk_your_table PRIMARY KEY (userid, column2, column3, column4) \nON ps_userid (userid);