DECLARE @strSQL NVARCHAR(MAX)\n\nSELECT @strSQL = 'SELECT FileName, path, size, vpath from "GRIP-SERVER"."Web2"..SCOPE() where FileName like ''reiseportal%'''\n\nSELECT @strSQL = 'SELECT DISTINCT DOC.ID_Kandidat, IDXS.* FROM OPENQUERY([GRIP-SERVER],'' + @strSQL + '') AS IDXS INNER JOIN tblK_Dokumente AS DOC ON DOC.Link = IDXS.[FileName] ORDER BY ID_Kandidat'\n\nEXEC sp_executesql @statement = @strSQL