CREATE TABLE binary_data_chunks (\n    object_id NUMBER,\n    chunk_sequence NUMBER,\n    chunk_data LONG RAW,\n    PRIMARY KEY (object_id, chunk_sequence)\n);\n\n-- Procedure to insert binary data in chunks\nCREATE OR REPLACE PROCEDURE insert_binary_data_in_chunks(\n    p_object_id NUMBER,\n    p_binary_data LONG RAW,\n    p_chunk_size NUMBER := 4000\n) AS\n    v_offset NUMBER := 1;\n    v_chunk_sequence NUMBER := 1;\nBEGIN\n    WHILE v_offset < LENGTH(p_binary_data) LOOP\n        INSERT INTO binary_data_chunks (object_id, chunk_sequence, chunk_data)\n        VALUES (p_object_id, v_chunk_sequence, \n                SUBSTR(p_binary_data, v_offset, p_chunk_size));\n        \n        v_offset := v_offset + p_chunk_size;\n        v_chunk_sequence := v_chunk_sequence + 1;\n    END LOOP;\nEND;\n/