SELECT *\nFROM (\n  SELECT *,\n    ROW_NUMBER() OVER (PARTITION BY CLIENT.CASE_OFFICE, CLIENT.REFERRAL_OFFICE ORDER BY OFFICE_MIS.TABLE_CODE) AS row_num\n  FROM CLIENT\n  LEFT OUTER JOIN OFFICE ON CLIENT.CASE_OFFICE = OFFICE.TABLE_CODE\n  LEFT OUTER JOIN OFFICE_MIS ON CLIENT.REFERRAL_OFFICE = OFFICE_MIS.TABLE_CODE\n) AS subquery\nWHERE row_num = 1;