SELECT CLIENT.CASE_OFFICE, CLIENT.REFERRAL_OFFICE, MAX(OFFICE_MIS.TABLE_CODE) AS table_code\nFROM CLIENT\nLEFT OUTER JOIN OFFICE ON CLIENT.CASE_OFFICE = OFFICE.TABLE_CODE\nLEFT OUTER JOIN OFFICE_MIS ON CLIENT.REFERRAL_OFFICE = OFFICE_MIS.TABLE_CODE\nGROUP BY CLIENT.CASE_OFFICE, CLIENT.REFERRAL_OFFICE;