CREATE TABLE property_owners (\n  owner_id NUMBER PRIMARY KEY,\n  first_name VARCHAR2(50),\n  last_name VARCHAR2(50)\n);\n\nCREATE TABLE property_details (\n  property_id NUMBER PRIMARY KEY,\n  owner_id NUMBER,\n  address VARCHAR2(200)\n);\n\nCREATE INDEX owner_info_idx ON property_owners (first_name, last_name)\nINDEXTYPE IS CTXSYS.CTXCAT;\n\nCREATE INDEX property_info_idx ON property_details (address)\nINDEXTYPE IS CTXSYS.CTXCAT;\n\nCREATE INDEX owner_details_idx ON (\n  SELECT owner_id, first_name || ' ' || last_name FROM property_owners\n  UNION ALL\n  SELECT owner_id, address FROM property_details\n) INDEXTYPE IS CTXSYS.CTXCAT;