CREATE EVENT SESSION [QueryCapture] ON SERVER \nADD EVENT sqlserver.query_store(\n    ACTION(sqlserver.sql_text, sqlserver.database_name)\n    WHERE sqlserver.database_name = 'YourDatabaseName'\n)\nADD TARGET package0.event_file(\n    SET filename = 'C:\QueryCapture.xel'\n)\nWITH (MAX_MEMORY = 4096 KB, EVENT_RETENTION_MODE = ALLOW_SINGLE_EVENT_LOSS, MAX_DISPATCH_LATENCY = 30 SECONDS, MAX_EVENT_SIZE = 0 KB, MEMORY_PARTITION_MODE = NONE, TRACK_CAUSALITY = OFF, STARTUP_STATE = ON)\nGO\n\nALTER EVENT SESSION [QueryCapture] ON SERVER STATE = START\nGO