-- Transaction 1 (snapshot isolation level)\nBEGIN TRANSACTION;\nSELECT * FROM mytable WHERE id = 1;  -- sees original data\n\n-- Transaction 2 (committed after Transaction 1 began)\nBEGIN TRANSACTION;\nUPDATE mytable SET value = 'new value' WHERE id = 1;\nCOMMIT;\n\n-- Transaction 1 (continues)\nSELECT * FROM mytable WHERE id = 1;  -- still sees original data\nUPDATE mytable SET value = 'another new value' WHERE id = 1;\nSELECT * FROM mytable WHERE id = 1;  -- sees the updated data\nCOMMIT;