DECLARE\n  v_file UTL_FILE.FILE_TYPE;\n  v_query VARCHAR2(4000) := 'SELECT * FROM your_table';\n  v_line VARCHAR2(4000);\n  v_directory VARCHAR2(30) := 'YOUR_DIRECTORY';\n  v_filename VARCHAR2(30) := 'output.txt';\n\nBEGIN\n  v_file := UTL_FILE.FOPEN(v_directory, v_filename, 'W');\n\n  FOR cur_rec IN EXECUTE IMMEDIATE v_query LOOP\n    v_line := cur_rec.column1 || ',' || cur_rec.column2 || ',' || cur_rec.column3;\n    UTL_FILE.PUT_LINE(v_file, v_line);\n  END LOOP;\n\n  UTL_FILE.FCLOSE(v_file);\nEND;