DECLARE @sql NVARCHAR(MAX);\nDECLARE @MyTableName sysname = 'MyTable';\n\nSET @sql = N'';\n\nSELECT \n    @sql += 'SELECT ''' + name + ''' WHERE EXISTS (SELECT 1 FROM [' + name + N'].sys.tables WHERE name = @MyTableName)' + CHAR(13) + CHAR(10) + 'UNION ALL' + CHAR(13) + CHAR(10)\nFROM \n    sys.databases \nWHERE \n    database_id > 4 AND state = 0;\n\nSET @sql = LEFT(@sql, LEN(@sql) - LEN('UNION ALL' + CHAR(13) + CHAR(10)));\n\nEXEC sp_executesql @sql, N'@MyTableName sysname', @MyTableName;