-- Create a temporary table with the new structure\nCREATE TABLE #MyTable_New (\n    ExistingColumn INT,\n    NewColumn INT\n);\n\n-- Migrate data from the existing table to the temporary table\nINSERT INTO #MyTable_New (ExistingColumn, NewColumn)\nSELECT ExistingColumn, NULL AS NewColumn\nFROM MyTable;\n\n-- Drop the existing table and rename the temporary table\nDROP TABLE MyTable;\nEXEC sp_rename '#MyTable_New', 'MyTable';