INSERT INTO NewTasks (TaskName, MilestoneID, CategoryID, SkillID, RateID)\nSELECT \n  t.TaskName,\n  (SELECT m.MilestoneID FROM NewMilestones m WHERE m.Name = t.MilestoneName),\n  (SELECT c.CategoryID FROM NewCategories c WHERE c.Name = t.CategoryName),\n  (SELECT s.SkillID FROM NewSkills s WHERE s.Name = t.SkillName),\n  (SELECT p.RateID FROM NewPayRates p WHERE p.Name = t.PayRateName)\nFROM OldTasks t;