ALTER TRIGGER [MyTableInsertDeleteUpdate] \n  ON  [dbo].[MyTable]\n  AFTER  INSERT,DELETE,UPDATE\nAS \nBEGIN\n    INSERT INTO audit_table (id, operation)\n    SELECT id, 'INSERT/UPDATE' FROM inserted;\n\n    -- Or, if you want to print the IDs\n    DECLARE @ids TABLE (id int);\n    INSERT INTO @ids (id)\n    SELECT id FROM inserted;\n\n    DECLARE @id int;\n    DECLARE cur CURSOR FOR SELECT id FROM @ids;\n    OPEN cur;\n\n    FETCH NEXT FROM cur INTO @id;\n    WHILE @@FETCH_STATUS = 0\n    BEGIN\n        PRINT 'inserted/updated id: ' + CAST(@id AS VARCHAR);\n        FETCH NEXT FROM cur INTO @id;\n    END\n\n    CLOSE cur;\n    DEALLOCATE cur;\nEND