CREATE TABLE UserFunctions (\n    Id INT PRIMARY KEY IDENTITY(1,1),\n    Name NVARCHAR(255) NOT NULL,\n    FunctionType NVARCHAR(50) NOT NULL,  -- e.g., "SimpleMathFunction"\n    Parameters XML NOT NULL,  -- store parameters as XML\n    Expression NVARCHAR(MAX) NOT NULL  -- store the function expression\n);\n\nCREATE TABLE FunctionParameters (\n    Id INT PRIMARY KEY IDENTITY(1,1),\n    UserFunctionId INT NOT NULL,\n    ParameterName NVARCHAR(255) NOT NULL,\n    ParameterType NVARCHAR(50) NOT NULL,  -- e.g., "Integer", "Float"\n    CONSTRAINT FK_FunctionParameters_UserFunctions FOREIGN KEY (UserFunctionId) REFERENCES UserFunctions(Id);\n);