-- Clean and transform data\nCREATE TABLE movies_clean AS\nSELECT \n    unique_name,\n    year,\n    CASE \n        WHEN unique_name LIKE '%(TV)' THEN 'TV'\n        WHEN unique_name LIKE '%(VG)' THEN 'VG'\n        ELSE 'Movie'\n    END AS type\nFROM movies_staging;\n\n-- Join data\nCREATE TABLE movies_joined AS\nSELECT \n    m.unique_name,\n    m.year,\n    m.type,\n    mp.mpaa_rating,\n    rt.running_time\nFROM movies_clean m\nJOIN mpaa_staging mp ON m.unique_name = mp.unique_name\nJOIN running_times_staging rt ON m.unique_name = rt.unique_name;