SELECT col1, col2, \n       subquery_result_1, \n       subquery_result_2\nFROM (\n  SELECT col1, col2, \n         CASE WHEN col3 = 'E01089001' THEN 1 ELSE 2 END AS subquery_result_1,\n         CASE WHEN col3 = 'E01089001' THEN 3 ELSE 4 END AS subquery_result_2\n  FROM Table1\n  WHERE col1 = 'A0529'\n);