SELECT d.Department, c.Category\nFROM (\n  SELECT Department, MAX(cnt) as max_cnt\n  FROM (\n    SELECT Department, Category, COUNT(*) as cnt\n    FROM your_table\n    GROUP BY Department, Category\n  ) sub\n  GROUP BY Department\n) d\nJOIN (\n  SELECT Department, Category, COUNT(*) as cnt\n  FROM your_table\n  GROUP BY Department, Category\n) c\nON d.Department = c.Department AND d.max_cnt = c.cnt