CREATE OR REPLACE PROCEDURE my_procedure AS\n  v_session_id VARCHAR2(255) := SYS_CONTEXT('USERENV', 'SESSIONID');\n  v_table_name VARCHAR2(255) := 'my_temp_table_' || v_session_id;\nBEGIN\n  EXECUTE IMMEDIATE 'CREATE GLOBAL TEMPORARY TABLE ' || v_table_name || ' (\n    id NUMBER,\n    data VARCHAR2(255)\n  ) ON COMMIT PRESERVE ROWS';\n\n  -- Use the temporary table\n  INSERT INTO v_table_name (id, data) VALUES (1, 'Some data');\n  -- ...\nEND;