SELECT 'INSERT INTO YOUR_TABLE_NAME (' || LISTAGG(column_name, ', ') WITHIN GROUP (ORDER BY column_id) || ') VALUES (' || LISTAGG("'" || column_value || "'", ', ') WITHIN GROUP (ORDER BY column_id) || ');' \nFROM   ( \n  SELECT column_name, column_value, column_id \n  FROM   ( \n    SELECT * \n    FROM   YOUR_TABLE_NAME \n  ) t \n  UNPIVOT ( \n    column_value FOR column_name IN ( \n      column1, column2, column3, ... \n    ) \n  ) \n  WHERE  ...  -- your query conditions here \n) \nGROUP BY table_name;