CREATE OR REPLACE FUNCTION encrypt_clob(p_clob IN CLOB) RETURN CLOB\nIS\n  encrypted_clob CLOB;\n  encryption_type PLS_INTEGER := DBMS_CRYPTO.ENCRYPT_AES256;\n  key RAW(32) := 'your_secret_key_here';  -- replace with your own key\nBEGIN\n  encrypted_clob := DBMS_CRYPTO.ENCRYPT(\n    src => UTL_RAW.CAST_TO_RAW(p_clob),\n    typ => encryption_type,\n    key => key);\n  RETURN encrypted_clob;\nEND;