CREATE ASSEMBLY [SqlServerProject1]\nFROM 'C:\path\to\SqlServerProject1.dll'\nWITH PERMISSION_SET = SAFE\nGO\n\nCREATE FUNCTION dbo.SplitClr (@string nvarchar(max), @delimiter nvarchar(10))\nRETURNS TABLE (value nvarchar(max))\nAS\nEXTERNAL NAME [SqlServerProject1].[SqlServerProject1.Split]\nGO\n\nDECLARE @LIST nvarchar(max) = 'Bob|Mary|Joe'\n\nSELECT u.*\nFROM Users u\nWHERE u.FirstName IN (SELECT value FROM dbo.SplitClr(@LIST, '|'))