-- Create a user-defined table type\nCREATE TYPE IdList AS TABLE (Id INT PRIMARY KEY);\n\n-- Create a stored procedure to perform the update\nCREATE PROCEDURE UpdateRecords\n    @Ids IdList READONLY\nAS\nBEGIN\n    UPDATE t\n    SET t.Property = 'New Value'\n    FROM TargetTable t\n    JOIN @Ids i ON t.Id = i.Id;\nEND;\n\n-- Execute the stored procedure with the list of IDs\nDECLARE @Ids IdList;\nINSERT INTO @Ids (Id) VALUES (1), (2), ..., (10000);\nEXEC UpdateRecords @Ids;